INSERT INTO `eb_rank_job` (`id`, `entid`, `name`, `cate_id`, `rank_id`, `card_id`, `job_count`, `describe`, `duty`, `status`, `created_at`, `updated_at`) VALUES
(1, 1, '总经理', 1, 1, 1, 1, '', '<p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-size: 18px;\"><strong><span style=\"letter-spacing: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">一、主持会议</span></strong></span><span style=\"font-size: 16px;\"><strong><span style=\"letter-spacing: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"></span></strong><span style=\"letter-spacing: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></span></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"line-height: 48px; letter-spacing: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">主持召开股东大会、董事会议，并负责上述会议的决议的贯彻落实。</span></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-size: 18px;\"><strong><span style=\"line-height: 48px; letter-spacing: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">二、发展规划</span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"line-height: 48px; letter-spacing: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">召集和主持管理委员会会议，组织讨论和决定公司的发展规划目标、经营方针、年度经营计划及日常经营工作中的重大事项。</span></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-size: 18px;\"><strong><span style=\"line-height: 48px; letter-spacing: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">三、决策审核</span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"line-height: 48px; letter-spacing: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">召集和主持产品开发、销售、市场动态，公司发展投入规划社会市场决策和可行性额算审核。</span></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-size: 18px;\"><strong><span style=\"line-height: 48px; letter-spacing: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">四、把控财务</span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"line-height: 48px; letter-spacing: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">主持财务资金的运行，筹集审批、审阅公司的财务报表和其它重要报表，全盘控制公司的财务活动状况。</span></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-size: 18px;\"><strong><span style=\"line-height: 48px; letter-spacing: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">五、高层管理</span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"line-height: 48px; letter-spacing: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">决定公司高层管理人员的聘用和解职、报酬、待遇和支付方式，并报董事会批准备案。签署批准调入公司的各级管理人员。</span></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-size: 18px;\"><strong><span style=\"line-height: 48px; letter-spacing: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">六、重要文件审核</span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"line-height: 48px; letter-spacing: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">签署对外上报、印发的各种重要报表、文件、资料。</span></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-size: 18px;\"><strong><span style=\"line-height: 48px; letter-spacing: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">七、重大事项处理</span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"line-height: 48px; letter-spacing: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">处理检督部门的管理工作的重大事项。</span></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-size: 18px;\"><strong><span style=\"line-height: 48px; letter-spacing: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">八、总结审核</span></strong></span></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"line-height: 48px; letter-spacing: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">组织审批公司实现规划目标业绩，主持公司内外的一切重大活动。</span></p>', 1, NULL, '2023-11-23 14:57:08'),
(2, 1, '副总经理', 1, 2, 1, 0, '', '<h2 style=\"white-space: normal; margin-top: 7px; margin-bottom: 7px; font-family: 宋体; font-size: 24px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">一</span></strong><strong><span style=\"font-family: 微软雅黑;\">、</span></strong><strong><span style=\"font-family: 微软雅黑;\">战略决策职责</span></strong></span></h2><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">1. 研究企业发展方向，审核公司发展战略;</span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">2. 负责公司重大经营决策的研究与审核;</span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">3. 对公司提出的重大投融资项目进行审核;</span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">4. 对公司年度投资计划及经营计划进行审核。</span></p><h2 style=\"white-space: normal; margin-top: 7px; margin-bottom: 7px; font-family: 宋体; font-size: 24px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">二</span></strong><strong><span style=\"font-family: 微软雅黑;\">、</span></strong><strong><span style=\"font-family: 微软雅黑;\">战略发展职责</span></strong></span></h2><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">1. 负责制订公司中长期发展战略;</span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">2. 负责制订公司战略规划流程，主持公司年度经营计划的制订，协助各分子公司制定战略规划和年度经营计划;</span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">3. 分析公司的经营现状以及各业务单位在行业内的地位，对公司已进入和将要进入的区域细分市场做出市场状况的分析;</span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">4. 收集国内外同行业先进企业资料，总结先进经营理念、管理体制、管理方法，提高内部管理水平，为公司提高核心竞争力、加强技术提供建设性意见;<br/>5. 根据战略规划的要求，组织和策划与国内外合作者的战略合作;</span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">6. 管理和维护公司的技术专利、知识产权等无形资产，对公司品牌进行统一协调管理。</span></p><h2 style=\"white-space: normal; margin-top: 7px; margin-bottom: 7px; font-family: 宋体; font-size: 24px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">三</span></strong><strong><span style=\"font-family: 微软雅黑;\">、</span></strong><strong><span style=\"font-family: 微软雅黑;\">经营管理职责</span></strong></span></h2><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">1. 负责公司资产管理、股权管理、经营审计;</span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">2. 负责公司与各分子公司之间的报告报审制度的建立、执行与监督;</span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">3. 负责总公司与各分子公司的日常工作协调及对其的运营监控、经营审计;</span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">4. 负责建立并完善公司经营管理制度;</span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">5. 负责公司重大投资事项的跟踪检查，帮助解决各种实际问题，协调各方关系;</span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">6. 检查公司年度经营计划执行情况;</span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">7. 定期收集各分子公司与董事会、股东会议案与决议、重大经营决策(投融资、产权变动)、预决算预案与草案、规划和计划、内部重大结构调整方案、阶段性工作总结和分析、重要人事变动等相关信息，送达集团公司各相关部门，并要求其形成相应报告，本部门做综合分析报集团公司总裁。</span></p><h2 style=\"white-space: normal; margin-top: 7px; margin-bottom: 7px; font-family: 宋体; font-size: 24px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">四、</span></strong><strong><span style=\"font-family: 微软雅黑;\">其他职责</span></strong></span></h2><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">1. 直属上级交办的其他工作；</span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">2. 对企业规划提出建设性的意见。</span></p>', 1, NULL, NULL),
(3, 1, '运营总监', 1, 3, 1, 0, '', '<p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 18px;\">1、团队制度与激励</span></strong></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">负责营销部相关规章制度的完善；根据团队销售目标，制定奖罚措施，考核标准；及时跟进检查落实，鼓舞激励，高效完成期初制定目标；</span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 18px;\">2、营销目标制定</span></strong></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">制定年度、季度、月度营销计划、运营计划、销售任务目标、销售方案、广告预算；并对月度任务进行分解，落实执行；</span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 18px;\">3、营销增长</span></strong></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">带领营销团队超额完成销售目标；确保官网访问量、源码安装量、新增用户量稳定、持续增长；</span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 18px;\">4、品牌宣传</span></strong></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">确保官方对外宣传渠道：官网、演示站、论坛、帮助文档、官方商城、服务市场、开源项目等宣传渠道，及客服接待系统畅通；对外宣传内容符合公司文化价值观、产品定位及最新销售政策；</span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 18px;\">5、营销活动策划落地实施</span></strong></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">与研发部密切配合，组织策划重大节日、产品发布等相关营销活动；监督实施、跟进，检查，总结、复盘营销活动，完善活动流程，提升团队协作效率；</span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 18px;\">6、推广整合</span></strong></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">组织部门内部、整合外部资源通过软文、渠道、分销、互换友链等多种方式推广公司产品；提示品牌曝光度及知名度；</span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 18px;\">7、客户维护</span></strong></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">维护良好的客情关系，及时处理客诉和危机事件，并制定应对策略，与相关部门沟通及时解决客户问题。提升售前、售中、售后服务质量；挖掘维护忠实客户、大客户；提升产品行业口碑；</span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 18px;\">8、团队目标激励</span></strong></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">制定团队销售目标，奖罚措施，考核标准，跟进检查，改进调整，鼓舞激励，高效完成期初制定目标；</span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 18px;\">9、广告投入</span></strong></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">根据每月广告预算，拓展付费推广渠道；根据投入产出比，细化广告投放方案，及时调整广告投放策略；</span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 18px;\">10、经营分析</span></strong></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">从广告投放、产品销量、用户增长、营业额等多个维度分析运营数据，发现运营中存在的问题，制定改进方案；</span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 18px;\">11、人才培养</span></strong></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">组织团队培训，传达落实公司企业文化；制定团队人才计划；建立团队传、帮、带机制，组建部门人才梯队；</span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 18px;\">12、其他工作</span></strong></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">完成上级主管交办的其他临时性工作,依据项目进度按时提交工作汇报。</span></p><p><br/></p>', 1, NULL, NULL),
(4, 1, '产品总监', 1, 3, 1, 0, '', '<p style=\"white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-size: 18px;\"><strong><span style=\"line-height: 48px; letter-spacing: 0px; background: rgb(255, 255, 255); font-family: 微软雅黑;\">一、</span></strong><strong><span style=\"line-height: 48px; letter-spacing: 0px; background: rgb(255, 255, 255); font-family: 微软雅黑;\">基本职责</span></strong></span></p><p style=\"white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-size: 16px;\">1、进行产品研发的需求分析，包括应用场景、行业技术、市场&amp;竞品、设备效益分析等，提出新产品研发方向。<br/>2、主导产品的设计工作，包括设备功能设计、关键技术参数设计、关键性能指标设计、产品呈现设计等。<br/>3、组织硬件、算法、软件、机械和周边配套产品研发等团队成员进行新产品的详细设计，完成产品设计工作。<br/>4、组织进行新产品的加工、装配、测试和优化工作，直至产品定型，完成产品技术标准的制定。<br/>5、进行产品全生命周期管理：包括产品线规划、信息管理（版本管理、BOM管理）等。&nbsp;<br/>6、基于成本控制、性能优化进行设备的优化迭代和升级工作。</span></p><h2 style=\"white-space: normal; font-family: 宋体; font-size: 24px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑; letter-spacing: 0px;\">二、&nbsp;其他职责</span></strong></span></h2><p style=\"white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-size: 16px;\">1、完成领导交付的其他任务；</span></p><p style=\"white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-size: 16px;\">2、学习最新技术，能将新技术用于项目中。</span></p><p><br/></p>', 1, NULL, NULL),
(5, 1, '项目总监', 1, 3, 1, 0, '', '<p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 48px; letter-spacing: 0px; background: rgb(255, 255, 255);\">1、实施交付</span></strong></span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-size: 16px;\"><span style=\"font-family: 微软雅黑; line-height: 48px; letter-spacing: 0px; background: rgb(255, 255, 255); font-size: 16px;\">负责公司项目实施交付管理工作，对项目总体负责，促进达成公司关键业绩目标；</span><span style=\"font-family: 微软雅黑; line-height: 48px; letter-spacing: 0px; background: rgb(255, 255, 255); font-size: 18px;\"><br/><strong>2、确保项目验收</strong></span></span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-size: 16px;\"><span style=\"font-family: 微软雅黑; line-height: 48px; letter-spacing: 0px; background: rgb(255, 255, 255); font-size: 16px;\">对各个项目实施方法，实施策略，实施方案提出合理化建议，确保项目按质按时验收；</span><span style=\"font-family: 微软雅黑; line-height: 48px; letter-spacing: 0px; background: rgb(255, 255, 255); font-size: 18px;\"><br/><strong>3、统筹、协调资源</strong></span></span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-size: 16px;\"><span style=\"font-family: 微软雅黑; line-height: 48px; letter-spacing: 0px; background: rgb(255, 255, 255); font-size: 16px;\">负责实施人员的管理工作，统筹调度部门内实施资源，协调跨部门资源；</span><span style=\"font-family: 微软雅黑; line-height: 48px; letter-spacing: 0px; background: rgb(255, 255, 255); font-size: 18px;\"><br/><strong>4、日常管理</strong></span></span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-size: 16px;\"><span style=\"font-family: 微软雅黑; line-height: 48px; letter-spacing: 0px; background: rgb(255, 255, 255); font-size: 16px;\">负责项目交付团队，业务督导，项目分配，能力提升，业务考核等日常工作；</span><span style=\"font-family: 微软雅黑; line-height: 48px; letter-spacing: 0px; background: rgb(255, 255, 255); font-size: 18px;\"><br/><strong>5、产品规划</strong></span></span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-size: 16px;\"><span style=\"font-family: 微软雅黑; line-height: 48px; letter-spacing: 0px; background: rgb(255, 255, 255); font-size: 16px;\">负责大客户及其组织对产品的定制化需求对接和业务长期需求规划，对客户业务交付和服务结果负责；</span><span style=\"font-family: 微软雅黑; line-height: 48px; letter-spacing: 0px; background: rgb(255, 255, 255); font-size: 18px;\"><br/><strong>6、成功案例知识库</strong></span></span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 48px; letter-spacing: 0px; background: rgb(255, 255, 255); font-size: 16px;\">提炼项目实施经验，形成项目实施案例，丰富实施知识库，总结提炼成功案例；</span></p><p style=\"margin-top: 20px; margin-bottom: 20px; white-space: normal; font-family: Calibri; line-height: 32px; background: rgb(255, 255, 255);\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 48px; letter-spacing: 0px;\">7、支撑售前</span></strong></span></p><p style=\"margin-top: 20px; margin-bottom: 20px; white-space: normal; font-family: Calibri; line-height: 32px; background: rgb(255, 255, 255);\"><span style=\"font-family: 微软雅黑; line-height: 48px; letter-spacing: 0px; font-size: 16px;\">根据项目交付案例，形成项目垂直行业解决方案，提高自有产品核心竞争力，支撑项目售前支撑工作；</span></p><p style=\"margin-top: 20px; margin-bottom: 20px; white-space: normal; font-family: Calibri; line-height: 32px; background: rgb(255, 255, 255);\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 48px; letter-spacing: 0px;\">8、客户维护</span></strong></span></p><p style=\"margin-top: 20px; margin-bottom: 20px; white-space: normal; font-family: Calibri; line-height: 32px; background: rgb(255, 255, 255);\"><span style=\"font-family: 微软雅黑; line-height: 48px; letter-spacing: 0px; font-size: 16px;\">加强交付过程中与客户的沟通交流，深入了解客户的需求，以需求为契机，引导客户后续项目立项；</span></p><p style=\"margin-top: 20px; margin-bottom: 20px; white-space: normal; font-family: Calibri; line-height: 32px; background: rgb(255, 255, 255);\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 48px; letter-spacing: 0px;\">9、制造品牌</span></strong></span></p><p style=\"margin-top: 20px; margin-bottom: 20px; white-space: normal; font-family: Calibri; line-height: 32px; background: rgb(255, 255, 255);\"><span style=\"font-family: 微软雅黑; line-height: 48px; letter-spacing: 0px; font-size: 16px;\">完善项目售后故障响应机制，打造品牌响应，推进后续维保合同续签。</span></p><p><br/></p>', 1, NULL, NULL),
(6, 1, '人事主管', 1, 4, 1, 0, '', '<p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">1、</span></strong><strong><span style=\"font-family: 微软雅黑;\">人事管理制度建设</span></strong></span><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑;\"></span></strong><strong><span style=\"font-family: 微软雅黑;\"></span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（1）根据公司人事管理需要，拟订人事基础管理制度、流程和表单，提交审核，为公司人事管理提供服务支持。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（2）监督各部门对于人事基础管理制度、流程及表单的执行情况，保证人事相关业务开展的规范与便捷。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">2、</span></strong><strong><span style=\"font-family: 微软雅黑;\">基础人事管理</span></strong></span><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑; font-size: 21px;\"></span></strong><strong><span style=\"font-family: 微软雅黑; font-size: 21px;\"></span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（1）开展员工录用、转正、调动、晋升、请假、离职、辞退、退休等工作，核实相关资料， 为相关人员提供人事业务办理服务。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（2）依据《岗位说明书》要求，收集存档相关岗位特殊工种证件等的原（复）件，及时督促相关人员到期证件的补办工作，确保相关岗位人员符合政府部门所要求的持证上岗条件。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（3）根据公司各部门培训需求及培训方案，协助完成培训实施工作。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（4）定期收集全员的培训需求，汇总并丰富培训内容。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（5）组织新员工培训并负责培训考核（培训达标率100%）；</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">3、</span></strong><strong><span style=\"font-family: 微软雅黑;\">员工档案管理</span></strong></span><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑; font-size: 21px;\"></span></strong><strong><span style=\"font-family: 微软雅黑; font-size: 21px;\"></span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（1）收集员工相关纸质材料存入档案，保持档案材料的及时入库，做好分类登记，按有关规定对档案进行例行的保养、管理或销毁。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（2）根据员工纸质档案信息，建立员工档案（电子）台账，及时更新员工信息。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（3）执行档案资料保密制度，按规定办理档案借阅登记手续，保证资料按时入库，完整无缺。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">4、</span></strong><strong><span style=\"font-family: 微软雅黑;\">考勤管理</span></strong></span><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑; font-size: 21px;\"></span></strong><strong><span style=\"font-family: 微软雅黑; font-size: 21px;\"></span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（1）根据考勤管理制度，监督检查考勤情况、工作汇报提交情况，并核查、整理各部门员工假单手续的完整性，确保员工月度考勤记录准确。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（2）结合各部门考勤明细，汇总上月员工考勤数据，核实数据真实性，整理团建基金明细，为薪酬核算提供依据。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">5、</span></strong><strong><span style=\"font-family: 微软雅黑;\">薪酬绩效管理</span></strong></span><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑; font-size: 21px;\"></span></strong><strong><span style=\"font-family: 微软雅黑; font-size: 21px;\"></span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（1）负责薪酬绩效制度的修订及执行落地，针对绩效实行效果提出合理化建议。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（2）按照时间节点组织公司员工绩效评定，监督部门负责人绩效辅导。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（3）负责月度员工工资核算，定期收集市场薪酬数据，完善公司薪酬福利制度。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">6、</span></strong><strong><span style=\"font-family: 微软雅黑;\">员工关系管理</span></strong></span><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑; font-size: 21px;\"></span></strong><strong><span style=\"font-family: 微软雅黑; font-size: 21px;\"></span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（1）根据《劳动合同法》开展员工劳动合同的签订、变更、续签、解除与维护工作，确保劳动关系管理规范化。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（2）依据《劳动合同法》、《人资管理制度》解答员工有关劳资关系的疑问，协调与处理公司常规性的劳动争议与劳动纠纷。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">7、</span></strong><strong><span style=\"font-family: 微软雅黑;\">招聘管理</span></strong></span><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑; font-size: 21px;\"></span></strong><strong><span style=\"font-family: 微软雅黑; font-size: 21px;\"></span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">完成招聘的基础工作，含招聘需求发布、简历筛选、面试安排、人员及时储备的工作。</span></p><p><br/></p>', 1, NULL, NULL),
(7, 1, '财务主管', 1, 4, 1, 0, '', '<p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">1、</span></strong><strong><span style=\"font-family: 微软雅黑;\">每日收支及产品销售明细统计</span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">财务每日核对前一日公司淘宝、小程序、支付宝、微信和公户进出账，别的途径进出账相关人员报给财务后，财务保证实时记录。 公司应尽量杜绝公账入私户，销售收入打款应提供公司指定账户，公司会不定期核对授权记录与销售记录确认是否一致。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">2、</span></strong><strong><span style=\"font-family: 微软雅黑;\">保证发票及时验旧核销、申领；及时开具</span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（1）发票申领：发票快用完后在电子税额局网站进行发票验旧核销，申领发票，申领后在金税盘读入发票。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（2）发票开具：需要开发票人员将需要开发票的相关订单发送给财务，（淘宝订单需要确认收货后方可开具发票）</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（3）发票合同邮寄。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">3、</span></strong><strong><span style=\"font-family: 微软雅黑;\">银行业务</span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">需要去银行办理的相关事宜及时与银行取得联系，约好业务办理时间及时办理。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">4、</span></strong><strong><span style=\"font-family: 微软雅黑;\">佣金结算</span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（1）系统里每份佣金及时兑付，线下返佣由销售人员提供订单号和情况说明</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（2）保证每份佣金核算准确且为推广员应当所得</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（3）核对每份佣金，发现问题及时向上级领导及周峰反应。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">5、</span></strong><strong><span style=\"font-family: 微软雅黑;\">备用金管理</span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（1）每个员工发生需报销费用需要向财务部门提供发票，确实不能提供发票的需要提供购买截图或者其他证明凭证。按月度报销，报销人员需填写费用报销单据，交财务审核再经许总审核后方可报销。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（2）财务每月将备用金使用明细及证明凭证整理好报许总审核。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">6、</span></strong><strong><span style=\"font-family: 微软雅黑;\">工资发放</span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">保证每月工资发放准确无误。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">7、</span></strong><strong><span style=\"font-family: 微软雅黑;\">月度收入支出流水整理</span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">在每日统计数据的基础上，月度终了将淘宝、微信商城、支付宝数据导出再次核对整理。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">8、</span></strong><strong><span style=\"font-family: 微软雅黑;\">月度财务报表出具</span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">月底编制公司资产负债表和利润表。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">9、</span></strong><strong><span style=\"font-family: 微软雅黑;\">月度项目报表出具</span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">月度终了编制每个项目利润表。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑;\">10、</span></strong><strong><span style=\"font-family: 微软雅黑;\">公司下月支出预算</span></strong><strong><span style=\"font-family: 微软雅黑;\"></span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">各部门关于下月支出预算报给财务，财务编制每月支出预算表。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">11、</span></strong><strong><span style=\"font-family: 微软雅黑;\">月度报税</span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">个人所得税在每月申报期前申报完成。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">12、</span></strong><strong><span style=\"font-family: 微软雅黑;\">月度记账凭证填制</span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（1）每月整理打印上月所有银行回单和所开具发票及其他相关原始凭证</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">（2）填制记账凭证，整理相关后附原始凭证，出具财务报表。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">13</span></strong><strong><span style=\"font-family: 微软雅黑;\">、</span></strong><strong><span style=\"font-family: 微软雅黑;\">季度报税</span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">在申报期前完成申报。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">登录电子税务局，根据每月及每个季度财务报表数据申报缴纳（增值税、城建税、教育费附加、地方教育费附加、水利基金、印花税、残保金、企业所得税）。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">14</span></strong><strong><span style=\"font-family: 微软雅黑;\">、</span></strong><strong><span style=\"font-family: 微软雅黑;\">高企季度调查监测表</span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">季度终了根据财务数据填写。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">15</span></strong><strong><span style=\"font-family: 微软雅黑;\">、</span></strong><strong><span style=\"font-family: 微软雅黑;\">年度汇算清缴</span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">在所得税汇算清缴限期前完成申报。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">登录电子税务局，根据上年度财务数据及上年度所得税申报缴纳情况完成所得税汇算清缴工作。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">16、</span></strong><strong><span style=\"font-family: 微软雅黑;\">工商年报</span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">登录工商年检网站，根据公司相关信息及经营情况完成工商年检工作。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">保证在下年度6.30日之前完成上年度公司工商年检工作。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">17、</span></strong><strong><span style=\"font-family: 微软雅黑;\">高企年报</span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">根据公司财务数据及公司各个项目（高新技术产品）情况填写。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">18、</span></strong><strong><span style=\"font-family: 微软雅黑;\">中小企业年报</span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">根据公司财务数据填写。</span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">19、</span></strong><strong><span style=\"font-family: 微软雅黑;\">其他</span></strong></span></p><p style=\"white-space: normal; font-family: 楷体_GB2312; font-weight: bold; font-size: 21px; line-height: 42.6667px;\"><span style=\"font-family: 微软雅黑; font-weight: normal; font-size: 16px;\">完成上级主管交办的其他临时性工作。</span></p><p><br/></p>', 1, NULL, NULL),
(8, 1, '行政主管', 1, 4, 1, 0, '', '<p><span style=\"font-size: 18px;\"><span style=\"font-family: 微软雅黑; background-color: rgb(255, 255, 255);\">1、协助部门经理开展工作，负责完善公司行政管理制度及规范并监督有效执行；</span><br style=\"font-family: 微软雅黑; font-size: 18px; white-space: normal;\"/><span style=\"font-family: 微软雅黑; background-color: rgb(255, 255, 255);\">2、负责公司级各类文件及管理制度的起草；</span><br style=\"font-family: 微软雅黑; font-size: 18px; white-space: normal;\"/><span style=\"font-family: 微软雅黑; background-color: rgb(255, 255, 255);\">3、负责公司级各类会议/重大活动的筹备、组织和实施；</span><br style=\"font-family: 微软雅黑; font-size: 18px; white-space: normal;\"/><span style=\"font-family: 微软雅黑; background-color: rgb(255, 255, 255);\">4、负责公司各类工作流程的梳理和完善；</span><br style=\"font-family: 微软雅黑; font-size: 18px; white-space: normal;\"/><span style=\"font-family: 微软雅黑; background-color: rgb(255, 255, 255);\">5、负责公司各类福利的筹备和发放；</span><br style=\"font-family: 微软雅黑; font-size: 18px; white-space: normal;\"/><span style=\"font-family: 微软雅黑; background-color: rgb(255, 255, 255);\">6、负责公司各类行政费用的预算、控制、节约等；</span><br style=\"font-family: 微软雅黑; font-size: 18px; white-space: normal;\"/><span style=\"font-family: 微软雅黑; background-color: rgb(255, 255, 255);\">7、负责公司各类公共区域的使用制度和管理规范；</span><br style=\"font-family: 微软雅黑; font-size: 18px; white-space: normal;\"/><span style=\"font-family: 微软雅黑; background-color: rgb(255, 255, 255);\">8、负责依据管理体系要求，协助部门经理推进各项体系管理要求；</span><br style=\"font-family: 微软雅黑; font-size: 18px; white-space: normal;\"/><span style=\"font-family: 微软雅黑; background-color: rgb(255, 255, 255);\">9、负责公司固定资产管理及特种设备管理；</span><br style=\"font-family: 微软雅黑; font-size: 18px; white-space: normal;\"/><span style=\"font-family: 微软雅黑; background-color: rgb(255, 255, 255);\">10、负责公司工作环境的提升与改进；</span><br style=\"font-family: 微软雅黑; font-size: 18px; white-space: normal;\"/><span style=\"font-family: 微软雅黑; background-color: rgb(255, 255, 255);\">11、 负责公司企业文化建设及宣传报道；</span><br style=\"font-family: 微软雅黑; font-size: 18px; white-space: normal;\"/><span style=\"font-family: 微软雅黑; background-color: rgb(255, 255, 255);\">12、执行上级交付的其它工作任务。</span></span></p>', 1, NULL, NULL),
(9, 1, '运营经理', 1, 4, 1, 0, '', '<p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 18px;\">1、对外官网展示</span></strong></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">负责官方网站、商城、论坛、客服接待、公众号、演示站、服务市场及一切对外展示的网页、系统的升级、改版、维护、审核工作；</span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 18px;\">2、品牌形象包装</span></strong></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">负责对外的公司、品牌、产品的文字、图片内容包装及对外宣传；提升公司行业口碑，建立对外品牌形象，提升产品核心竞争力；</span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 18px;\">3、演示站维护</span></strong></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">负责所有产品演示站用户体验提升；</span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 18px;\">4、活动策划执行</span></strong></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">根据产品发布、节假日、公司庆典等重大活动的策划执行；</span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 18px;\">5、宣传推广</span></strong></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">负责公司网站SEO优化及产品的日常宣传推广工作；</span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 18px;\">6、客户调研</span></strong></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">负责公司客户群体满意度调研；分析、优化服务流程，提升用户口碑；</span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 18px;\">7、其他工作</span></strong></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; font-size: 16px;\">完成上级领导交代的其它事宜。</span></p><p><br/></p>', 1, NULL, NULL),
(10, 1, '产品主管', 1, 4, 1, 0, '', '<p style=\"white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-size: 18px;\"><strong><span style=\"line-height: 48px; letter-spacing: 0px; background: rgb(255, 255, 255); font-family: 微软雅黑;\">一、</span></strong><strong><span style=\"line-height: 48px; letter-spacing: 0px; background: rgb(255, 255, 255); font-family: 微软雅黑;\">基本职责</span></strong></span></p><p style=\"white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-size: 16px;\">1、进行产品研发的需求分析，包括应用场景、行业技术、市场&amp;竞品、设备效益分析等，提出新产品研发方向。<br/>2、主导产品的设计工作，包括设备功能设计、关键技术参数设计、关键性能指标设计、产品呈现设计等。<br/>3、组织硬件、算法、软件、机械和周边配套产品研发等团队成员进行新产品的详细设计，完成产品设计工作。<br/>4、组织进行新产品的加工、装配、测试和优化工作，直至产品定型，完成产品技术标准的制定。<br/>5、进行产品全生命周期管理：包括产品线规划、信息管理（版本管理、BOM管理）等。&nbsp;<br/>6、基于成本控制、性能优化进行设备的优化迭代和升级工作。</span></p><h2 style=\"white-space: normal; font-family: 宋体; font-size: 24px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑; letter-spacing: 0px;\">二、&nbsp;其他职责</span></strong></span></h2><p style=\"white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-size: 16px;\">1、完成领导交付的其他任务；</span></p><p style=\"white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-size: 16px;\">2、学习最新技术，能将新技术用于项目中。</span></p><p><br/></p>', 1, NULL, NULL);
INSERT INTO `eb_rank_job` (`id`, `entid`, `name`, `cate_id`, `rank_id`, `card_id`, `job_count`, `describe`, `duty`, `status`, `created_at`, `updated_at`) VALUES
(11, 1, '测试主管', 1, 4, 1, 0, '', '<h2 style=\"white-space: normal; margin-top: 7px; margin-bottom: 7px; font-family: 宋体; font-size: 24px; margin-left: 0px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px;\">1. 主要职责</span></strong></span></h2><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-size: 16px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; background: rgb(255, 255, 255);\">（1）负责带领团队完成项目测试工作；</span><span style=\"font-family: 微软雅黑; line-height: 42.6667px; color: rgb(81, 88, 109); letter-spacing: 0px; background: rgb(255, 255, 255);\"><br/></span><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; background: rgb(255, 255, 255);\">（2）负责项目各阶段的跨部门协作与评审工作，根据项目实际情况制定测试方案、计划，安排组内成员任务分工，确保测试工作按时、按质完成；</span><span style=\"font-family: 微软雅黑; line-height: 42.6667px; color: rgb(81, 88, 109); letter-spacing: 0px; background: rgb(255, 255, 255);\"><br/></span><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; background: rgb(255, 255, 255);\">（3）负责测试方案、计划，安排测试用例的编写，并进行组内、组外用例评审；</span><span style=\"font-family: 微软雅黑; line-height: 42.6667px; color: rgb(81, 88, 109); letter-spacing: 0px; background: rgb(255, 255, 255);\"><br/></span><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; background: rgb(255, 255, 255);\">（4）负责测试环境的搭建、维护工作，负责Git权限管理、项目备份等工作；</span><span style=\"font-family: 微软雅黑; line-height: 42.6667px; color: rgb(81, 88, 109); letter-spacing: 0px; background: rgb(255, 255, 255);\"><br/></span><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; background: rgb(255, 255, 255);\">（5）负责软件功能、安全性、性能、自动化等方面的测试工作；</span><span style=\"font-family: 微软雅黑; line-height: 42.6667px; color: rgb(81, 88, 109); letter-spacing: 0px; background: rgb(255, 255, 255);\"><br/></span><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; background: rgb(255, 255, 255);\">（6）负责确保项目按需保质保量的按时完成，并且输出测试报告；</span><span style=\"font-family: 微软雅黑; line-height: 42.6667px; color: rgb(81, 88, 109); letter-spacing: 0px; background: rgb(255, 255, 255);\"><br/></span><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; background: rgb(255, 255, 255);\">（7）负责协助运维进行项目部署、更新、上线工作；</span><span style=\"font-family: 微软雅黑; line-height: 42.6667px; color: rgb(81, 88, 109); letter-spacing: 0px; background: rgb(255, 255, 255);\"><br/></span><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; background: rgb(255, 255, 255);\">（8）负责测试团队的管理、定期技术分享、定期技术培训、团队建设；</span><span style=\"font-family: 微软雅黑; line-height: 42.6667px; color: rgb(81, 88, 109); letter-spacing: 0px; background: rgb(255, 255, 255);\"><br/></span><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; background: rgb(255, 255, 255);\">（9）完成中心领导交办的工作。</span></span></p><h2 style=\"white-space: normal; margin-top: 7px; margin-bottom: 7px; font-family: 宋体; font-size: 24px; margin-left: 0px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px;\">2. 技术管理</span></strong></span></h2><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-size: 16px;\">（1）参与实施软件质量保证工作，促进公司软件质量体系建设、持续改进；</span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-size: 16px;\">（2）学习当前市场上流行的测试技术，并要求适应于产品整体测试；</span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-size: 16px;\">（3）参与分析产品在测试过程的经验和教训，纳入测试资产库，为后期项目提供有效经验和数据。</span></p><h2 style=\"white-space: normal; margin-top: 7px; margin-bottom: 7px; font-family: 宋体; font-size: 24px; margin-left: 0px; line-height: 48px;\"><span style=\"font-size: 18px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-weight: normal;\">3.&nbsp;</span><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px;\">其他职责</span></strong></span></h2><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-size: 16px;\">（1）对软件测试技术管理；</span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-size: 16px;\">（2）完成领导交付的其他任务。</span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-size: 16px;\">（3）学习最新技术，能将新技术用于项目中；</span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-size: 16px;\">（4）完善测试部内部流程和规范。</span></p><p><br/></p>', 1, NULL, NULL),
(12, 1, '项目经理', 1, 4, 1, 0, '', '<p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-size: 18px;\"><span style=\"font-family: 微软雅黑; letter-spacing: 0px; font-weight: bold; background: rgb(255, 255, 255);\">一、</span><strong><span style=\"line-height: 42.6667px; letter-spacing: 0px; background: rgb(255, 255, 255); font-family: 微软雅黑;\">基本职责</span></strong></span></p><p style=\"margin-top: 9px; margin-bottom: 9px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-size: 16px;\">对接客户，了解项目需求，进行项目管理，对整体的交付结果负责，主要职责包括：<br/>1、按照管理范围，与对口的客户经理共同进行客户对接，确保沟通顺畅，维系良好的客户关系和满意度，并进行需求挖掘；<br/>2、对项目进行全流程，按时、保质、保量交付项目成果。包括项目分析、计划制定、资源配置、任务分配、风险识别、过程监控、问题解决以及成本控制、文档与知识管理及保密管理等；<br/>3、专兼职团队建设与人才培训、带教、培养，建设高效能团队；<br/>4、统计、登记、数据分析与结算管理；<br/>5、试译、试标、返修管理、客户满意度管理、CSR制作、大客户服务能力提升等客户服务相关工作。</span></p><h2 style=\"white-space: normal; margin-top: 9px; margin-bottom: 9px; font-family: 宋体; font-size: 24px; margin-left: 0px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px;\">二、&nbsp;其他职责</span></strong></span></h2><p style=\"margin-top: 9px; margin-bottom: 9px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-size: 16px;\">1、完成领导交付的其他任务；</span></p><p style=\"margin-top: 9px; margin-bottom: 9px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-size: 16px;\">2、学习最新技术，能将新技术用于项目中。</span></p><p><br/></p>', 1, NULL, NULL),
(13, 1, '部门经理', 1, 4, 1, 0, '', '<p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; background: rgb(255, 255, 255);\">一、基本职责</span></strong></span></p><p style=\"white-space: normal; font-family: Calibri; font-size: 14px; line-height: 28px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; background: rgb(255, 255, 255); font-size: 16px;\">1. 负责部门内部的日常管理工作。<br/>2. 负责与其他部门的协调对接配合工作。<br/>3. 负责监督本部门员工的规范服务、工作技能，职业道德。<br/>4. 负责跟踪单源，协调与维护客户关系。<br/>5. 负责部门的任务下达，制定任务目标、执行原则及部门考核制度。<br/>6. 负责对新员工进行工作流程的培训及日常指导工作。<br/>7. 负责安排编制部门人员的工作职责，根据公司的年度计划、项目计划编制相关计划，并实行。<br/>8. 负责开展部门的输入、验证、输出、确认等相关评审工作，控制项目及产品的方向与质量，把握部门发展方向。<br/>9. 了解部门人员的优缺点，并根据个人技能和成长计划安排部门人员工作。<br/>10. 领导项目中的方法及解决方案。<br/>11. 协助上级领导处理日常相关协调问题及配合其他部门工作。<br/>12. 负责把握项目开发过程中节点控制，与项目团队一起定期组织开发进展会议，及时调整项目开发计划。<br/>13. 组织项目结题会议，总结开发总的问题、经验、组织人员对文档成果和技术成果的总结与归档。<br/>14. 了解行业流程，并会编写相关设计方案，与客户保持近距离沟通与联系。<br/>15. 有较强的执行力和协调能力，能及时处理相关问题及协调配合其他部门人员调动问题。</span></p><h2 style=\"white-space: normal; margin-top: 9px; margin-bottom: 9px; font-family: 宋体; font-size: 24px; margin-left: 0px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-weight: normal;\">二、&nbsp;</span><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px;\">其他职责</span></strong></span></h2><p style=\"margin-top: 9px; margin-bottom: 9px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-size: 16px;\">1. 完成领导交付的其他任务；</span></p><p style=\"margin-top: 9px; margin-bottom: 9px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px; font-size: 16px;\">2. 学习最新技术，能将新技术用于项目中。</span></p><p><br/></p>', 1, NULL, NULL),
(14, 1, '客服主管', 1, 4, 1, 0, '', '<p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑; line-height: 48px;\">1. 目标制定</span></strong></span></p><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px;\">协助客服经理制定团队成员月度销售目标；自己达成销售目标的同时，确保团队成员销售目标达成，带领团队实现业绩突破；</span></p><h2 style=\"white-space: normal; margin-top: 7px; margin-bottom: 7px; font-family: 宋体; font-size: 24px; margin-left: 0px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">2. 制度落实</span></strong></span></h2><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px;\">负责落实团队岗位职责、部门制度；按时完成工作总结、绩效制定、评定；兑现团队奖励、处罚制度；</span></p><h2 style=\"white-space: normal; margin-top: 7px; margin-bottom: 7px; font-family: 宋体; font-size: 24px; margin-left: 0px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">3. 团队培训</span></strong></span></h2><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px;\">负责组织团队成员了解产品功能、熟悉产品知识，培训日常工作流程、销售技巧；提升团队成员业务能力；</span></p><h2 style=\"white-space: normal; margin-top: 7px; margin-bottom: 7px; font-family: 宋体; font-size: 24px; margin-left: 0px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">4. 工作检查</span></strong></span></h2><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px;\">定期检查团队成员聊天记录、客户跟踪情况、客户回访记录，发现问题及时引导纠正；了解团队成员重点意向客户，协助团队成员达成客户成交；</span></p><h2 style=\"white-space: normal; margin-top: 7px; margin-bottom: 7px; font-family: 宋体; font-size: 24px; margin-left: 0px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">5. 人才培养</span></strong></span></h2><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px;\">负责团队成员培训，及时完善优化客服话术；定时培训、辅导、检查修正，提高团队人员工作能力；通过传、帮、带机制，组建团队门人才梯队；</span></p><h2 style=\"white-space: normal; margin-top: 7px; margin-bottom: 7px; font-family: 宋体; font-size: 24px; margin-left: 0px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">6. 客户维护</span></strong></span></h2><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px;\">定期检查团队成员用户管理情况，确保团队成员按照相关要求，做好客户分组、打好用户标签；检查工作微信的朋友圈运营情况，落实团队成员活动期间消息群发、活动推文转发、朋友圈宣传工作；</span></p><h2 style=\"white-space: normal; margin-top: 7px; margin-bottom: 7px; font-family: 宋体; font-size: 24px; margin-left: 0px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">7. 问题处理</span></strong></span></h2><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px;\">负责团队成员日常工作遇到的问题处理；及时处理客诉，异常客户上报；与相关部门沟通及时解决客户问题；</span></p><h2 style=\"white-space: normal; margin-top: 7px; margin-bottom: 7px; font-family: 宋体; font-size: 24px; margin-left: 0px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">8. 值班安排</span></strong></span></h2><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px;\">协助客服经理做好重大活动、周末、节假日工作安排；</span></p><h2 style=\"white-space: normal; margin-top: 7px; margin-bottom: 7px; font-family: 宋体; font-size: 24px; margin-left: 0px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">9. 团队激励</span></strong></span></h2><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px;\">关注团队成员工作状态，做好团队激励，鼓舞团队士气；按时完成团队绩效考核制定、绩效评分；</span></p><h2 style=\"white-space: normal; margin-top: 7px; margin-bottom: 7px; font-family: 宋体; font-size: 24px; margin-left: 0px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">10. 用户体验</span></strong></span></h2><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px;\">确保团队成员所负责的产品演示站顺利访问，演示站产品、幻灯片、活动产品开启、添加美化；确保演示站后台权限完整性，提升演示站用户体验；</span></p><h2 style=\"white-space: normal; margin-top: 7px; margin-bottom: 7px; font-family: 宋体; font-size: 24px; margin-left: 0px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">11. 问题收集</span></strong></span></h2><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32px;\"><span style=\"font-family: 微软雅黑; line-height: 42.6667px; letter-spacing: 0px;\">负责团队成员负责项目论坛回复、客户回复，问题建议收集及时提交TAPD并与项目负责人反馈处理问题；</span></p><h2 style=\"white-space: normal; margin-top: 7px; margin-bottom: 7px; font-family: 宋体; font-size: 24px; margin-left: 0px; line-height: 48px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 微软雅黑;\">12. 日报查看</span></strong></span></h2><p style=\"margin-top: 7px; margin-bottom: 7px; white-space: normal; font-family: Calibri; line-height: 32